const TILE_COUNT = 1024;

const str_color = (str) => {
	if (!str) return `#111`

	let hash = 0;
	for (let i = 0; i < str.length; i++) {
		hash = str.charCodeAt(i) + ((hash << 5) - hash);
	}

	let color = `#`;
	for (let i = 0; i < 3; i++) {
		const value = (hash >> (i * 8)) & 0xFF;
		color += (`00` + value.toString(16)).substr(-2);
	}
	
	return color
};

